import React, { useRef } from "react";
import emailjs from "@emailjs/browser";

const Email = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_u5fnsok",
        "template_vqt2qjj",
        form.current,
        "7fdhzUjrx1NoHKnAk"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <div className="mail" id="by-email">
      <form ref={form} onSubmit={sendEmail}>
        <label className="form-label">Name</label>
        <input className="form-control" type="text" name="user_name" placeholder="Your name" />
        <label className="form-label">Email</label>
        <input className="form-control" type="email" name="user_email" placeholder="Your email"/>
        <label className="form-label">Message</label>
        <textarea className="form-control" name="message" rows="5" placeholder="Message" />
        <input type="submit" value="Send" className="my-3 email-btn"/>
      </form>
    </div>
  );
};

export default Email;
